# renamespringer.py
# reformat book names such as
# "Acquired Brain Injury - Jean Elbaum, Deborah Benson, 2007 - 978-0-387-37575-5.pdf"
# 2021-01-09    PV

import os
import shutil

source = r'W:\Livres\A_Trier\Springer_Free_Ebooks'
#dest = r'W:\Livres\A_Trier\Springer_Renamed'
dest=r"c:\temp\Springer_Renamed"

def DoCopy(s: str, d: str):
    print(d)
    dfolder = os.path.dirname(d)
    if not os.path.exists(dfolder):
        os.makedirs(dfolder)
    shutil.copy(s, d)

def Process(sf: str, df: str):
    if os.path.exists(os.path.join(source, sf+".pdf")):
        DoCopy(os.path.join(source, sf+".pdf"), os.path.join(dest, df+".pdf"))
    if os.path.exists(os.path.join(source, sf+".epub")):
        DoCopy(os.path.join(source, sf+".epub"), os.path.join(dest, df+".epub"))

