# 2021-09-05    PV      Just simple code to retrieve flags

import sys
import requests

def getflag(href, title, alt, srcset, src):
    print(title)
    resp = requests.get(src.replace('250','500'))
    img = resp.content
    path = 'C:\\Downloads\\Flags\\'+title+'.png'
    with open(path, 'wb') as fp:
        fp.write(img)


